                {shortDescription}
                <span className="text-[blue]" >{readMore ? "Read More" : "Show Less" }</span>
            </div>
            Price : {price }
            {/* <div> */}
                {/* <button onClick={()=> removeHandler(id)}>Not Interested</button> */}
            {/* </div> */}
        </div>
    )
}
