import React from "react"

export default function Cards( {id,name,image,info,price , removeHandler}) {

    const [readMore, setReadMore] = React.useState(false)
    const shortDescription = `${info.substring(0,200)}...`
    const val = readMore ? shortDescription : info
    const [description , setDescription] = React.useState(shortDescription)
    function readMoreHandler() {
        setReadMore(prevState => !prevState)
        setReadMore
    }

    return (
        <div card>
            Name:{name}  
            <img src={image} width="150px"/>
            <div className="text-[12px]">
                {description}
                <span className="text-[blue]" onClick={readMoreHandler} >{readMore ? "Read More" : "Show Less" }</span><br/>
            Price : {price }
            </div>
            <div className="text-[blue]">
                <button onClick={()=> removeHandler(id)}>Not Interested</button>
            </div><br/>
        </div>
    )
}
